import React, { useState } from 'react';
import GenreCard from '../components/GenreCard';
import AudioPlayer from '../components/AudioPlayer';
import { getTracksByGenre, searchTracks } from '../api';

const genres = ["pop", "rock", "jazz", "hiphop", "electronic", "classical", "reggae"];

const Home = () => {
    const [tracks, setTracks] = useState([]);
    const [selectedTrack, setSelectedTrack] = useState(null);

    const handleGenreClick = async (genre) => {
        const result = await getTracksByGenre(genre);
        setTracks(result);
    };

    const handleSearch = async (query) => {
        const result = await searchTracks(query);
        setTracks(result);
    };

    return (
        <div className="home-page with-audio-player">
            <div className="home-container">
                {/* Genre Section */}
                <div className="section genre-section">
                    <div className="section-header">
                        <h2 className="section-title">Genres</h2>
                        <div className="section-decoration"></div>
                    </div>
                    <div className="genre-grid">
                        {genres.map((g) => (
                            <div key={g} className="genre-card" onClick={() => handleGenreClick(g)}>
                                <img src={`https://source.unsplash.com/160x100/?${g}`} alt={g} />
                                <div className="genre-overlay">
                                    <h3>{g}</h3>
                                </div>
                                <div className="genre-glow"></div>
                            </div>
                        ))}
                    </div>
                </div>

                {/* Track Section */}
                <div className="section track-section">
                    <div className="section-header">
                        <h2 className="section-title">Tracks</h2>
                        <div className="section-decoration"></div>
                    </div>
                    <div className="track-grid">
                        {tracks.map((t) => (
                            <div key={t.id} className="track-card" onClick={() => setSelectedTrack(t)}>
                                <div className="track-image-container">
                                    <img src={t.album_image} alt={t.name} className="track-image" />
                                    <div className="track-overlay">
                                        <div className="play-button">▶</div>
                                    </div>
                                </div>
                                <div className="track-info">
                                    <p className="track-name">{t.name}</p>
                                    <p className="track-artist">{t.artist_name}</p>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </div>

            {/* Audio Player */}
            <AudioPlayer track={selectedTrack} />
        </div>
    );
};

export default Home;
